INSERT INTO `plants` (`id`, `name`, `scientific_name`, `description`, `sunshine`, `difficulty`, `image_url`, `position`, `location`, `characteristics`, `traditional_uses`, `cultural_significance`, `well_being`, `fun_fact`) VALUES
('1', 'Rose', 'Rosa × odorata', 'La rose est une fleur élégante et parfumée, symbole de l\'amour et de la passion. Ses pétales délicats et son parfum envoûtant en font l\'une des fleurs les plus appréciées au monde.', 'Plein soleil', 'Modérée', 'https://images.pexels.com/photos/56866/garden-rose-red-pink-56866.jpeg', 0x00000000010100000076e09c11a56d4840a835cd3b4ed10240, 'Jardin des Tuileries', '{\"minerals\": [\"Fer\", \"Calcium\", \"Magnésium\"], \"vitamins\": [\"Vitamine C\"], \"antioxidants\": [\"Polyphénols\", \"Flavonoïdes\"], \"fragranceEffect\": \"Apaisante et relaxante\", \"therapeuticUses\": [\"Aide à réduire le stress\", \"Améliore la qualité du sommeil\", \"Calme les irritations cutanées\"]}', '{\"tea\": \"Les pétales de rose sont utilisés pour préparer des thés apaisants et aromatiques.\", \"essentialOil\": \"L\'huile essentielle de rose est prisée en aromathérapie pour ses propriétés relaxantes.\", \"cosmetic\": \"Les extraits de rose sont utilisés dans des produits de soins pour la peau pour leurs propriétés hydratantes et anti-âge.\"}', 'Symbole de l\'amour et de la beauté dans de nombreuses cultures à travers le monde.', '{\"stressRelief\": \"Son parfum réduit les niveaux de stress et favorise une sensation de calme.\", \"moodEnhancer\": \"Améliore l\'humeur et stimule les émotions positives.\", \"skinCare\": \"Aide à apaiser et hydrater la peau sensible.\"}', 'Les roses sont cultivées depuis plus de 5 000 ans et étaient déjà appréciées dans les jardins de la Chine antique.'),
('10', 'Lys', 'Lilium', 'Avec ses grandes fleurs parfumées, le lys incarne l\'élégance et la pureté. Il est parfait pour décorer les jardins et les intérieurs.', 'Mi-ombre', 'Modérée', 'https://media.istockphoto.com/id/105813631/fr/photo/lys-blanc-dans-un-jardin.jpg?s=612x612&w=0&k=20&c=Vcp397EmXefWLiG1wp43KWFbgTTIwpGPf21BL-XCimY=', 0x000000000101000000c3f5285c8f424440295c8fc2f5284040, 'Parc Monceau', '{\"minerals\":[],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Améliore l\'humeur grâce à son parfum doux\"],\"fragranceEffect\":\"Élégant et floral\"}', '{\"religious\":\"Utilisé comme symbole de pureté dans l\'art et la religion.\",\"gifts\":\"Souvent offert lors de mariages ou d\'événements religieux.\",\"gardens\":\"Apporte une touche majestueuse aux espaces extérieurs.\"}', 'Symbole de la royauté dans l\'héraldique française.', '{\"moodEnhancement\":\"Son parfum stimule les émotions positives.\",\"decor\":\"Évoque un sentiment de sérénité et de luxe.\",\"tradition\":\"Reflète des valeurs de noblesse et de pureté.\"}', 'Le lys blanc est souvent associé à la Vierge Marie dans la tradition chrétienne.'),
('11', 'Hortensia', 'Hydrangea', 'L\'hortensia est un arbuste florissant dont les couleurs varient selon le pH du sol, allant du bleu au rose.', 'Mi-ombre', 'Modérée', 'https://img.freepik.com/photos-premium/fleurs-hortensia-bleu-bouchent_196038-693.jpg', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Parc André Citroën', '{\"minerals\":[\"Aluminium (selon le sol)\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Apaisant pour les yeux grâce à ses couleurs douces\"],\"fragranceEffect\":\"Léger et subtil\"}', '{\"landscaping\":\"Idéal pour créer des massifs colorés.\",\"cutFlowers\":\"Utilisé dans les bouquets pour sa longévité.\",\"gardens\":\"Favorise un charme rustique ou sophistiqué.\"}', 'Considéré comme un symbole de gratitude et de beauté.', '{\"aesthetics\":\"Crée une atmosphère apaisante.\",\"seasonal\":\"Offre un spectacle changeant selon les saisons.\",\"decoration\":\"Parfait pour les arrangements floraux durables.\"}', 'La couleur des hortensias peut être modifiée en ajustant le pH du sol.'),
('12', 'Purple Dome', 'Aster novae-angliae', 'Le Purple Dome est une variété d\'aster compact et robuste, particulièrement apprécié pour sa floraison abondante de couleur violette intense qui illumine les jardins en fin de saison.', 'Plein soleil', 'Facile', 'https://gardengoodsdirect.com/cdn/shop/files/aster-woods-purple-29777631739946_grande.jpg?v=1695417540', 0x000000000101000000e17a14ae47464440295c8fc2f5282940, 'Jardin des Plantes, Paris', '{\"minerals\":[\"Fer\",\"Calcium\"],\"vitamins\":[],\"antioxidants\":[\"Flavonoïdes\"],\"therapeuticUses\":[\"Attire les pollinisateurs\",\"Améliore la biodiversité du jardin\"],\"fragranceEffect\":\"Délicat et herbacé\"}', '{\"gardens\":\"Excellent pour les massifs et bordures automnales\",\"bouquets\":\"Apprécié en fleurs coupées pour sa longue tenue\",\"pollinator\":\"Attire les papillons et les abeilles\"}', 'Symbolise l\'élégance et la persévérance dans le langage des fleurs', '{\"gardenHealth\":\"Contribue à la biodiversité du jardin\",\"aesthetics\":\"Apporte une touche de couleur vibrante en fin de saison\",\"wildlife\":\"Soutient la faune locale, notamment les pollinisateurs\"}', 'Contrairement à beaucoup d\'autres asters, le Purple Dome reste compact et n\'a pas besoin d\'être tuteuré'),
('13', 'Jonquille', 'Narcissus jonquilla', 'La jonquille est l\'un des premiers messagers du printemps, avec ses fleurs jaunes éclatantes et parfumées qui émergent dès la fin de l\'hiver.', 'Plein soleil à mi-ombre', 'Facile', 'https://media.istockphoto.com/id/1176574947/fr/photo/fleurs-jaunes-de-jonquille.jpg?s=612x612&w=0&k=20&c=ADY7FnrCfo7mhjD2uKL_DpHkvIhQgtxmt3YselQitQw=', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Parc de Bagatelle, Paris', '{\"minerals\":[\"Calcium\",\"Potassium\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Symbole du renouveau printanier\"],\"fragranceEffect\":\"Doux et printanier\"}', '{\"celebrations\":\"Symbole traditionnel du printemps et du renouveau\",\"gardens\":\"Excellente pour la naturalisation dans les pelouses\",\"cutFlowers\":\"Populaire en bouquets printaniers\"}', 'Représente l\'espoir et le renouveau dans de nombreuses cultures', '{\"moodBoost\":\"Les couleurs vives stimulent l\'optimisme\",\"seasonalMarker\":\"Annonce l\'arrivée du printemps\",\"fragrance\":\"Son parfum délicat évoque la fraîcheur printanière\"}', 'Les bulbes de jonquilles contiennent des substances toxiques qui les protègent naturellement des rongeurs'),
('14', 'Jacinthe', 'Hyacinthus orientalis', 'La jacinthe est une plante bulbeuse réputée pour ses fleurs parfumées en forme de grappe et sa palette de couleurs variées.', 'Plein soleil à mi-ombre', 'Modérée', 'https://images.ctfassets.net/b85ozb2q358o/95dd345e42affef5d6f6387dcde452fb1be0f9a51863a3fa605a023940ea62b9/9746b55f9623bf93850dffe214848276/quand-comment-planter-bulbes-fleurs-4.jpg', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Jardin du Luxembourg, Paris', '{\"minerals\":[\"Phosphore\",\"Potassium\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Aromathérapie naturelle\",\"Améliore l\'ambiance\"],\"fragranceEffect\":\"Intense et suave\"}', '{\"indoor\":\"Populaire pour le forçage en intérieur\",\"gardens\":\"Création de bordures parfumées\",\"aromatherapy\":\"Utilisée pour son parfum apaisant\"}', 'Symbole du printemps et de la renaissance dans la mythologie grecque', '{\"aromatherapy\":\"Le parfum aide à la relaxation\",\"seasonal\":\"Apporte de la joie pendant la période hivernale\",\"decoration\":\"Embellit les espaces intérieurs et extérieurs\"}', 'La jacinthe peut être forcée à fleurir en hiver sur un simple vase d\'eau'),
('15', 'Jasmin jaune', 'Jasminum nudiflorum', 'Le jasmin d\'hiver est un arbuste grimpant qui fleurit en plein hiver, apportant une touche de couleur dorée quand la plupart des plantes sont en dormance.', 'Plein soleil à mi-ombre', 'Facile', 'https://resize.elle.fr/article/var/plain_site/storage/images/deco/exterieur/jardin/le-jasmin-d-hiver-couleur-et-parfum-en-hiver-4191280/101392595-1-fre-FR/Le-jasmin-d-hiver-couleur-et-parfum-en-hiver.jpg', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Parc Monceau, Paris', '{\"minerals\":[\"Fer\",\"Magnésium\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Égaye l\'ambiance hivernale\"],\"fragranceEffect\":\"Subtil et doux\"}', '{\"landscaping\":\"Excellent pour couvrir les murs et treillages\",\"winterGarden\":\"Apporte de la couleur en hiver\",\"decoration\":\"Utilisé pour les décorations hivernales\"}', 'Symbole de joie et d\'optimisme en période hivernale', '{\"winterCheer\":\"Combat la morosité hivernale\",\"gardenInterest\":\"Maintient la vie au jardin en hiver\",\"aesthetics\":\"Crée des points focaux dorés dans le jardin\"}', 'Cette plante peut fleurir même sous la neige, d\'où son surnom de \"soleil d\'hiver\"'),
('16', 'Bruyère', 'Erica', 'La bruyère est une plante rustique qui offre des fleurs colorées pendant l\'hiver, idéale pour les jardins de rocaille et les massifs.', 'Plein soleil à mi-ombre', 'Modérée', 'https://blog.rouepepinieres.com/hs-fs/hubfs/Erica-Eva-gold-bruy%C3%A8re-couvre-sol.jpg?width=1000&height=667&name=Erica-Eva-gold-bruy%C3%A8re-couvre-sol.jpg', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Jardin des Serres d\'Auteuil, Paris', '{\"minerals\":[\"Fer\",\"Calcium\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Décorative et couvre-sol\"],\"fragranceEffect\":\"Léger et boisé\"}', '{\"groundcover\":\"Excellent couvre-sol pour zones acides\",\"winterColor\":\"Apporte de la couleur en hiver\",\"landscaping\":\"Idéal pour les jardins de rocaille\"}', 'Symbolise la solitude et la protection dans le folklore écossais', '{\"lowMaintenance\":\"Facile à entretenir une fois établie\",\"yearRound\":\"Procure un intérêt visuel toute l\'année\",\"wildlife\":\"Attire les abeilles et les papillons\"}', 'La bruyère peut survivre dans des conditions extrêmes et pousse naturellement sur les landes et les sols pauvres'),
('2', 'Aloe Vera', 'Aloe barbadensis miller', 'L\'Aloe Vera est une plante succulente prisée pour ses propriétés médicinales et cosmétiques. Sa sève gélatineuse est utilisée depuis des siècles pour apaiser et soigner divers maux.', 'Lumière indirecte ou soleil doux', 'Facile', 'https://img.freepik.com/premium-photo/aloe-vera-leaf-hd-stock-photographic-image-with-drops-water-gel-herbal-medecine_820616-1419.jpg', 0x000000000101000000b01bbb44f37a4140da37f08672d35dc0, 'Jardin botanique de Los Angeles', '{\"minerals\":[\"Calcium\",\"Potassium\",\"Sodium\"],\"vitamins\":[\"Vitamine A\",\"Vitamine C\",\"Vitamine E\"],\"antioxidants\":[\"Anthraquinones\"],\"therapeuticUses\":[\"Hydrate et apaise la peau\",\"Favorise la digestion\",\"Stimule la cicatrisation\"],\"fragranceEffect\":\"Légèrement purifiant et frais\"}', '{\"gel\":\"Utilisé pour apaiser les coups de soleil, les brûlures légères et les irritations cutanées.\",\"juice\":\"Consommé pour favoriser une digestion saine.\",\"cosmetic\":\"Ingrédient populaire dans les produits pour la peau et les cheveux.\"}', 'Utilisé dans la médecine traditionnelle égyptienne comme \"plante de l\'immortalité\".', '{\"skinCare\":\"Aide à réduire les inflammations et hydrate intensément.\",\"digestion\":\"Améliore la santé intestinale grâce à ses enzymes naturelles.\",\"detox\":\"Contribue à éliminer les toxines du corps.\"}', 'L\'Aloe Vera est capable de survivre plusieurs semaines sans eau grâce à sa capacité à stocker l\'humidité dans ses feuilles.'),
('3', 'Lavande', 'Lavandula angustifolia', 'La lavande est une plante aromatique emblématique des régions méditerranéennes, connue pour son parfum relaxant et ses nombreuses vertus thérapeutiques.', 'Plein soleil', 'Facile', 'https://www.creativefabrica.com/wp-content/uploads/2022/12/16/Lavender-Photography-HDR-HD-8K-4K-1080p-Sharp-Focus-55mm-52729637-1.png', 0x000000000101000000e17a14ae474644408fc2f5285c0f1640, 'Plateau de Valensole, France', '{\"minerals\":[\"Fer\",\"Magnésium\"],\"vitamins\":[\"Vitamine C\"],\"antioxidants\":[\"Linalool\",\"Linalyl acétate\"],\"therapeuticUses\":[\"Réduit le stress\",\"Améliore le sommeil\",\"Apaise les maux de tête\"],\"fragranceEffect\":\"Relaxant et équilibrant\"}', '{\"tea\":\"Préparée pour apaiser l\'esprit et favoriser un sommeil réparateur.\",\"essentialOil\":\"Utilisée en aromathérapie pour soulager l\'anxiété et la tension.\",\"sachets\":\"Placée dans les armoires pour parfumer et éloigner les insectes.\"}', 'Symbole de pureté et de calme dans les cultures méditerranéennes.', '{\"stressRelief\":\"Son arôme aide à réduire l\'anxiété et à calmer l\'esprit.\",\"sleepAid\":\"Favorise un sommeil profond et réparateur.\",\"insectRepellent\":\"Naturellement efficace contre les moustiques et autres nuisibles.\"}', 'Les Romains utilisaient la lavande pour parfumer leurs bains publics et leurs vêtements.'),
('4', 'Menthe', 'Mentha spicata', 'La menthe est une plante vivace appréciée pour sa fraîcheur et son arôme revigorant. Elle est largement utilisée en cuisine et en médecine traditionnelle.', 'Mi-ombre', 'Facile', 'https://www.shutterstock.com/shutterstock/videos/3445557255/thumb/1.jpg', 0x000000000101000000a8c64b37894544400ad7a3703d8a2940, 'Villa d\'Este, Rome', '{\"minerals\":[\"Potassium\",\"Fer\"],\"vitamins\":[\"Vitamine A\",\"Vitamine C\"],\"antioxidants\":[\"Menthol\",\"Rosmarinic acid\"],\"therapeuticUses\":[\"Favorise la digestion\",\"Apaise les douleurs musculaires\",\"Combat les nausées\"],\"fragranceEffect\":\"Revitalisant et purifiant\"}', '{\"tea\":\"Infusion populaire pour calmer l\'estomac et rafraîchir l\'haleine.\",\"oil\":\"Utilisée pour apaiser les douleurs musculaires et les maux de tête.\",\"culinary\":\"Ajoutée aux plats et boissons pour une touche de fraîcheur.\"}', 'Associée à l\'hospitalité dans de nombreuses cultures.', '{\"digestion\":\"Réduit les ballonnements et favorise la santé intestinale.\",\"freshness\":\"Rafraîchit l\'esprit et stimule la concentration.\",\"painRelief\":\"Efficace contre les douleurs légères grâce à ses propriétés analgésiques.\"}', 'La menthe est une plante si robuste qu\'elle peut envahir tout un jardin si elle n\'est pas contenue.'),
('5', 'Camomille', 'Matricaria chamomilla', 'La camomille est une plante douce et apaisante, souvent utilisée en infusion pour ses propriétés relaxantes et digestives.', 'Plein soleil', 'Facile', 'https://img.freepik.com/photos-premium/macro-champ-camomille_97070-864.jpg', 0x000000000101000000e17a14ae47464440295c8fc2f5282940, 'Berlin Botanical Gardens', '{\"minerals\":[\"Phosphore\",\"Potassium\"],\"vitamins\":[\"Vitamine A\"],\"antioxidants\":[\"Apigénine\",\"Flavonoïdes\"],\"therapeuticUses\":[\"Soulage les insomnies\",\"Calme les inflammations\",\"Aide à la digestion\"],\"fragranceEffect\":\"Apaisant et légèrement sucré\"}', '{\"tea\":\"Infusion classique pour se détendre et favoriser le sommeil.\",\"skin\":\"Préparée en lotion pour apaiser les irritations cutanées.\",\"aromatherapy\":\"Utilisée pour réduire l\'anxiété et favoriser un sentiment de bien-être.\"}', 'Connue comme \"la médecine des plantes\" dans les traditions européennes.', '{\"relaxation\":\"Favorise un état de calme et réduit l\'anxiété.\",\"digestion\":\"Apaise les troubles gastriques légers.\",\"skinCare\":\"Réduit les rougeurs et calme les inflammations.\"}', 'Les anciens Égyptiens considéraient la camomille comme sacrée et l\'offraient à leurs dieux.'),
('6', 'Fougère', 'Polypodiopsida', 'La fougère est une plante luxuriante qui évoque une ambiance tropicale. Ses frondes délicates apportent une touche de fraîcheur et d\'élégance dans les espaces ombragés.', 'Ombre ou lumière indirecte', 'Modérée', 'https://media.istockphoto.com/id/655052762/fr/photo/vert-foug%C3%A8re.jpg?s=612x612&w=0&k=20&c=OWUCTCq4x639Kgb6DsALpncGulRPB3DgFmBf7zMPGlg=', 0x000000000101000000ee7c3f355e474440c3f5285c8f024040, 'Parc Montsouris', '{\"minerals\":[\"Magnésium\",\"Fer\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Purifie l\'air ambiant\",\"Favorise l\'humidité dans l\'espace\"],\"fragranceEffect\":\"Effet naturel et apaisant\"}', '{\"indoor\":\"Utilisée pour décorer les intérieurs grâce à son feuillage dense.\",\"gardens\":\"Idéale pour les rocailles ombragées.\",\"airPurification\":\"Connue pour améliorer la qualité de l\'air.\"}', 'Symbolise la résilience et la vitalité dans plusieurs cultures.', '{\"airQuality\":\"Aide à purifier l\'air et à réduire les toxines.\",\"stressRelief\":\"Apporte une ambiance sereine et apaisante.\",\"decor\":\"Crée un environnement harmonieux et naturel.\"}', 'Les fougères existent depuis plus de 360 millions d\'années et précèdent les plantes à fleurs !'),
('7', 'Orchidée', 'Orchidaceae', 'L\'orchidée est une fleur exotique et élégante, très appréciée pour ses couleurs vibrantes et sa beauté unique. Elle symbolise la beauté et l\'élégance.', 'Lumière indirecte', 'Difficile', 'https://cdn.pixabay.com/photo/2023/02/04/21/32/flowers-7768218_640.jpg', 0x0000000001010000001f85eb51b8464440b81e85eb51084040, 'Jardin des Serres d\'Auteuil', '{\"minerals\":[\"Calcium\",\"Magnésium\"],\"vitamins\":[\"Vitamine C\"],\"antioxidants\":[\"Polyphénols\",\"Flavonoïdes\"],\"fragranceEffect\":\"Soutien émotionnel et apaisant\",\"therapeuticUses\":[\"Aide à améliorer la concentration\",\"Soulage la fatigue mentale\",\"Stimule les capacités cognitives\"]}', '{\"tea\":\"Certaines espèces d\'orchidées sont utilisées dans des tisanes pour leurs propriétés énergisantes.\",\"essentialOil\":\"L\'huile essentielle d\'orchidée est utilisée pour ses propriétés relaxantes et régénératrices.\",\"cosmetic\":\"L\'extrait d\'orchidée est utilisé dans des produits de soin pour ses propriétés hydratantes et anti-âge.\"}', 'L\'orchidée est un symbole de beauté, de sophistication et de luxe dans de nombreuses cultures.', '{\"stressRelief\":\"Sa fragrance subtile aide à réduire les niveaux de stress et favorise la relaxation.\",\"moodEnhancer\":\"Stimule la créativité et améliore l\'humeur générale.\",\"skinCare\":\"Les extraits d\'orchidée nourrissent et rajeunissent la peau, en la laissant douce et éclatante.\"}', 'Les orchidées peuvent vivre plusieurs années et certaines espèces sont connues pour leur capacité à se multiplier facilement dans les conditions appropriées.'),
('8', 'Bambou', 'Bambusoideae', 'Le bambou est une plante robuste et élégante, idéale pour créer des haies ou décorer les jardins zen. Sa croissance rapide en fait une plante unique.', 'Mi-ombre', 'Modérée', 'https://png.pngtree.com/background/20230513/original/pngtree-close-up-shot-of-a-bamboo-forest-picture-image_2507777.jpg', 0x000000000101000000c3f5285c8f424440295c8fc2f5284040, 'Parc des Buttes-Chaumont', '{\"minerals\":[\"Silice\",\"Calcium\"],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Atténue les tensions grâce à son ambiance relaxante\"],\"fragranceEffect\":\"Zen et rafraîchissant\"}', '{\"construction\":\"Utilisé pour construire des structures légères et durables.\",\"decor\":\"Apporte une touche asiatique et naturelle aux jardins.\",\"erosionControl\":\"Protège le sol de l\'érosion grâce à ses racines denses.\"}', 'Considéré comme un symbole de longévité et de force en Asie.', '{\"relaxation\":\"Crée un environnement paisible pour la méditation.\",\"ecoFriendly\":\"Contribue à une culture durable grâce à sa croissance rapide.\",\"aesthetics\":\"Idéal pour les espaces extérieurs zen ou modernes.\"}', 'Certaines variétés de bambou peuvent pousser jusqu\'à un mètre par jour.'),
('9', 'Tulipe', 'Tulipa', 'Symbole du printemps, la tulipe est une fleur éclatante aux multiples couleurs qui égaye les jardins avec sa beauté intemporelle.', 'Plein soleil', 'Modérée', 'https://png.pngtree.com/thumb_back/fh260/background/20230724/pngtree-tv-wallpaper-pink-tulips-wallpapers-image_10176785.jpg', 0x000000000101000000c3f5285c8f424440295c8fc2f5283f40, 'Champ de Mars', '{\"minerals\":[],\"vitamins\":[],\"antioxidants\":[],\"therapeuticUses\":[\"Apporte une sensation de joie et de renouveau\"],\"fragranceEffect\":\"Subtil et printanier\"}', '{\"bouquets\":\"Très prisée pour les arrangements floraux.\",\"celebrations\":\"Offerte en symbole d\'amour ou d\'amitié.\",\"festivals\":\"Fleur emblématique de nombreux festivals printaniers.\"}', 'Appréciée pour son rôle central dans la \"tulipomanie\" du XVIIe siècle.', '{\"moodBoost\":\"Élève l\'humeur avec ses couleurs vives.\",\"decoration\":\"Idéale pour orner jardins et intérieurs.\",\"natureConnection\":\"Favorise un lien avec la nature au printemps.\"}', 'La tulipe est originaire de Turquie et a été importée en Europe au XVIe siècle.');

